mapper.update:
UPDATE card_fund_contract
`contract_no` = #{contractNo}, `consignor_account_id` = #{consignorAccountId}, `start_time` = #{startTime}, `end_time` = #{endTime}, `state` = #{state}, `signature_image_path` = #{signatureImagePath}, `creator_id` = #{creatorId}, `creator` = #{creator}, `terminater` = #{terminater}, `terminate_notes` = #{terminateNotes}, `terminate_time` = NOW(), `firm_id` = #{firmId}, `firm_name` = #{firmName}, `notes` = #{notes}, `modify_time` = NOW()
WHERE id = #{id}

mapper.select:
SELECT
	`id`, 
	`contract_no`, 
	`consignor_card_no`,
	`consignor_account_id`, 
	`consignor_customer_code`,
	`consignor_customer_id`,
	`start_time`, 
	`end_time`, 
	`state`, 
	`creator`, 
	`create_time`,
	`firm_id`
FROM
	card_fund_contract
WHERE
	firm_id = #{firmId}
	
		AND contract_no = #{contractNo}
	
	
		AND consignor_account_id = #{consignorAccountId} 
	
	
		AND consignor_customer_id = #{consignorCustomerId} 
	
	
		AND consignor_customer_code = #{consignorCustomerCode}
	
	
		AND contract_no IN (SELECT contract_no FROM card_fund_consignor WHERE consignee_id_mobile = #{consigneeMobile})
	
	
		AND state = #{state}
	
	
		AND create_time >= #{createStartTime}
	
	
		AND create_time <= #{createEndTime}
	
	
		AND end_time = #{expirationTime}
	
	AND state != -1
ORDER BY create_time DESC
